//Made By Ali Ramadhanpackage mainimport (	"fmt"	"math/rand"	"os"	"time"	"math/big"	"bufio"	crypto_rand "crypto/rand")var Chidori    	  = []string{}func GetInt(max int) (int, error) {	if max <= 0 {		return 0, fmt.Errorf("can't define input as <=0")	}	nbig, err := crypto_rand.Int(crypto_rand.Reader, big.NewInt(int64(max)))	if err != nil {		return max, err	}	n := int(nbig.Int64())	return n, err}func Choice(j []string) (string, error) {	i, err := GetInt(len(j))	if err != nil {		return "", err	}	return j[i], nil}func main() {	rand.Seed(time.Now().UTC().UnixNano())	Hinata, err := os.Open("LeeVersion/LeeMaya.txt")	if err != nil {        fmt.Println(err)    }    defer Hinata.Close()    Sakura := bufio.NewScanner(Hinata)    for Sakura.Scan(){    	Raikiri := strings.Split((Sakura.Text()),"] ")[1]    	Rasengan := strings.Replace(Raikiri, ">","",1)    	Chidori = append(Chidori, Rasengan)    }    if err := Sakura.Err(); err != nil {    	fmt.Println(err)    }    Rinnegan, _ := Choice(Chidori)    Kagebunshin := strings.Split((Rinnegan),":21278")[0]    Chidori = Kagebunshin    fmt.Println(Chidori)}